package com.training.home_work.lesson02;

public class Task05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * 5. Дан прямоугольник, ширина которого в два раза меньше длины. Найти площадь
		 * прямоугольника
		 */

		double a = 10;
		double b = a / 2;
		double s = a * b;

		System.out.printf("                   a = %s", a);
		System.out.println();
		System.out.println("		 ---------------");
		System.out.println("		|               |");
		System.out.printf("		|               | b = %s", b);
		System.out.println();
		System.out.println("		|               |");
		System.out.println("		 ---------------");
		System.out.printf("		   S = %s  ", s);

	}

}
